<script>
    $(document).ready(function() {
        function isUrl(s) {
            var regexp = /(ftp|http|https):\/\/(\w+:{0,1}\w*@)?(\S+)(:[0-9]+)?(\/|\/([\w#!:.?+=&%@!\-\/]))?/
            return regexp.test(s);
        }
        $(document).off("click", ".link").on('click', ".link", function(e) {
            e.preventDefault();
            var weblink = $(this).attr('plink');

            isUrl(weblink) ? window.open(weblink, '_blank') : '';
        });
    });
</script>